import React, { useState } from "react";
import axios from "axios";
import "./SearchSection.css";

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleChange = event => {
    setSearchTerm(event.target.value);
  };

  const searchCarparks = () => {
    try {
      const response = axios.get(`http://datamall2.mytransport.sg/ltaodataservice/CarParkAvailabilityv2${searchTerm}`);
      const data = response.data;
      setSearchResults(data.results);
    } catch (error) {
      console.error('Error fetching carpark data.: ', error);
    }
  };

  return (
    <div>
      <input type="text" 
      placeholder="Search" 
      value={searchTerm} 
      onChange={handleChange} />
      <button onClick={searchCarparks}>Search</button>
      <SearchResults carparkLocations={searchResults} />
    </div>
  );
};

const SearchResults = ({ carparkLocations }) => {
  return (
    <div>
      {carparkLocations.map((location) => (
        <div key={location.id}>
          <h2>{location.Development}</h2>
          <p>Available Lots: {location.AvailableLots}</p>
          <p>Lot Type: {location.LotType}</p>
        </div>
      ))}
    </div>
  );
};

export default SearchResults;
